import { useQuery } from "@tanstack/react-query";
import { useRoute } from "wouter";
import { Card, CardContent } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Avatar, AvatarFallback, AvatarImage } from "@/components/ui/avatar";
import { Loader2, Trophy, Award, Sparkles } from "lucide-react";
import { format } from "date-fns";

interface SharedAchievementData {
  achievement: {
    tier: string;
    icon: string;
    name: string;
    description: string;
  };
  user: {
    firstName: string | null;
    lastName: string | null;
    profileImageUrl: string | null;
  };
  unlockedAt: string | null;
}

export default function ShareAchievement() {
  const [, params] = useRoute("/share/achievement/:id");
  const userAchievementId = params?.id;

  const { data, isLoading, error} = useQuery<SharedAchievementData>({
    queryKey: [`/api/achievements/share/${userAchievementId}`],
    enabled: !!userAchievementId, // Only fetch if ID is present
  });

  // Handle missing ID parameter
  if (!userAchievementId) {
    return (
      <div className="min-h-screen bg-background flex items-center justify-center p-4">
        <Card className="max-w-md w-full" data-testid="card-not-found">
          <CardContent className="p-8 text-center space-y-4">
            <Trophy className="h-12 w-12 text-muted-foreground mx-auto" />
            <div>
              <h2 className="text-xl font-semibold" data-testid="text-not-found-title">Achievement Not Found</h2>
              <p className="text-muted-foreground mt-2" data-testid="text-not-found-description">
                This achievement link may be invalid or has been removed.
              </p>
            </div>
          </CardContent>
        </Card>
      </div>
    );
  }

  if (isLoading) {
    return (
      <div className="min-h-screen bg-background flex items-center justify-center">
        <div className="text-center">
          <Loader2 className="h-8 w-8 animate-spin text-primary mx-auto" />
          <p className="text-muted-foreground mt-4" data-testid="text-loading">Loading achievement...</p>
        </div>
      </div>
    );
  }

  if (error || !data) {
    return (
      <div className="min-h-screen bg-background flex items-center justify-center p-4">
        <Card className="max-w-md w-full" data-testid="card-not-found">
          <CardContent className="p-8 text-center space-y-4">
            <Trophy className="h-12 w-12 text-muted-foreground mx-auto" />
            <div>
              <h2 className="text-xl font-semibold" data-testid="text-not-found-title">Achievement Not Found</h2>
              <p className="text-muted-foreground mt-2" data-testid="text-not-found-description">
                This achievement link may be invalid or has been removed.
              </p>
            </div>
          </CardContent>
        </Card>
      </div>
    );
  }

  const { achievement, user, unlockedAt } = data;
  const userName = [user.firstName, user.lastName].filter(Boolean).join(" ") || "Someone";
  const initials = `${user.firstName?.[0] || ""}${user.lastName?.[0] || ""}`.toUpperCase() || "?";

  const tierColors = {
    bronze: "text-amber-700 dark:text-amber-600 border-amber-700/50 dark:border-amber-600/50 bg-amber-950/20 dark:bg-amber-950/30",
    silver: "text-slate-400 dark:text-slate-300 border-slate-400/50 dark:border-slate-300/50 bg-slate-900/20 dark:bg-slate-900/30",
    gold: "text-yellow-500 dark:text-yellow-400 border-yellow-500/50 dark:border-yellow-400/50 bg-yellow-950/20 dark:bg-yellow-950/30",
    platinum: "text-cyan-400 dark:text-cyan-300 border-cyan-400/50 dark:border-cyan-300/50 bg-cyan-950/20 dark:bg-cyan-950/30",
  };

  const tierColor = tierColors[achievement.tier as keyof typeof tierColors] || tierColors.bronze;

  return (
    <div className="min-h-screen bg-background">
      {/* Header */}
      <header className="border-b border-border bg-card/30 backdrop-blur-sm">
        <div className="container max-w-4xl mx-auto px-4 py-4">
          <div className="flex items-center gap-2">
            <div className="w-10 h-10 rounded-full bg-primary flex items-center justify-center">
              <Sparkles className="h-5 w-5 text-primary-foreground" />
            </div>
            <h1 className="text-2xl font-bold text-foreground">2Mins</h1>
          </div>
        </div>
      </header>

      {/* Main Content */}
      <main className="container max-w-2xl mx-auto px-4 py-12">
        <div className="space-y-8">
          {/* Achievement Card */}
          <Card className="overflow-hidden" data-testid="card-shared-achievement">
            <div className={`absolute inset-0 ${tierColor.split(' ').find(c => c.startsWith('bg-'))} pointer-events-none opacity-50`} />
            <CardContent className="relative p-8 space-y-6">
              {/* Achievement Icon and Tier */}
              <div className="flex items-start justify-between">
                <div className={`p-4 rounded-lg border ${tierColor}`}>
                  {achievement.icon === "Trophy" ? (
                    <Trophy className={`h-8 w-8 ${tierColor.split(' ')[0]}`} />
                  ) : (
                    <Award className={`h-8 w-8 ${tierColor.split(' ')[0]}`} />
                  )}
                </div>
                <Badge
                  variant="outline"
                  className={`${tierColor} capitalize text-base px-4 py-1`}
                  data-testid="badge-tier"
                >
                  {achievement.tier}
                </Badge>
              </div>

              {/* Achievement Details */}
              <div className="space-y-3">
                <h2 className="text-2xl font-bold" data-testid="text-achievement-name">
                  {achievement.name}
                </h2>
                <p className="text-muted-foreground text-lg">
                  {achievement.description}
                </p>
              </div>

              {/* User Info */}
              <div className="pt-4 border-t border-border">
                <div className="flex items-center gap-3">
                  <Avatar className="h-12 w-12">
                    <AvatarImage src={user.profileImageUrl || undefined} />
                    <AvatarFallback>{initials}</AvatarFallback>
                  </Avatar>
                  <div>
                    <p className="font-medium" data-testid="text-user-name">
                      {userName}
                    </p>
                    <p className="text-sm text-muted-foreground">
                      Unlocked {unlockedAt ? format(new Date(unlockedAt), "MMMM d, yyyy") : "recently"}
                    </p>
                  </div>
                </div>
              </div>
            </CardContent>
          </Card>

          {/* Call to Action */}
          <Card>
            <CardContent className="p-6 text-center space-y-4">
              <div className="w-16 h-16 rounded-full bg-primary/10 flex items-center justify-center mx-auto">
                <Sparkles className="h-8 w-8 text-primary" />
              </div>
              <div>
                <h3 className="text-xl font-semibold mb-2">
                  Start Your Journey
                </h3>
                <p className="text-muted-foreground">
                  Join 2Mins and unlock achievements by completing 2-minute challenges across physical, mental, learning, finance, and relationship categories.
                </p>
              </div>
            </CardContent>
          </Card>
        </div>
      </main>
    </div>
  );
}

client/src/pages/ChallengeDetail.tsx.content:
import { useQuery, useMutation } from "@tanstack/react-query";
import { useRoute, useLocation } from "wouter";
import { CircularTimer } from "@/components/CircularTimer";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { ArrowLeft, CheckCircle2 } from "lucide-react";
import { categoryConfig, difficultyConfig } from "@/lib/categories";
import type { Challenge } from "@shared/schema";
import { useState } from "react";
import { apiRequest, queryClient } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";

export default function ChallengeDetail() {
  const [, params] = useRoute("/challenge/:id");
  const [, setLocation] = useLocation();
  const { toast } = useToast();
  const [isComplete, setIsComplete] = useState(false);
  const [timeSpent, setTimeSpent] = useState(0);
  const [pointsEarned, setPointsEarned] = useState(0);

  const challengeId = params?.id;

  // Fetch challenge details
  const { data: challenge, isLoading } = useQuery<Challenge>({
    queryKey: ["/api/challenges", challengeId],
    enabled: !!challengeId,
  });

  // Complete challenge mutation
  const completeMutation = useMutation({
    mutationFn: async (data: { timeSpent: number }) => {
      return apiRequest("POST", `/api/challenges/${challengeId}/complete`, data);
    },
    onSuccess: (data: any) => {
      setPointsEarned(data.pointsEarned || challenge?.points || 0);
      setIsComplete(true);

      // Invalidate queries to refresh data
      queryClient.invalidateQueries({ queryKey: ["/api/progress"] });
      queryClient.invalidateQueries({ queryKey: ["/api/history"] });
      queryClient.invalidateQueries({ queryKey: ["/api/challenges/random"] });
      queryClient.invalidateQueries({ queryKey: ["/api/achievements/user"] });
      queryClient.invalidateQueries({ queryKey: ["/api/analytics/daily?days=30"] });
      queryClient.invalidateQueries({ queryKey: ["/api/analytics/category"] });
      queryClient.invalidateQueries({ queryKey: ["/api/analytics/weekly"] });
      queryClient.invalidateQueries({ queryKey: ["/api/analytics/monthly"] });

      // Show challenge completion toast
      toast({
        title: "Challenge Complete!",
        description: `You earned ${data.pointsEarned || challenge?.points} points!`,
      });

      // Show achievement unlock toasts
      if (data.newAchievements && data.newAchievements.length > 0) {
        data.newAchievements.forEach((achievement: any, index: number) => {
          setTimeout(() => {
            toast({
              title: "Achievement Unlocked!",
              description: `${achievement.name}: ${achievement.description}`,
              duration: 5000,
            });
          }, (index + 1) * 1000); // Stagger notifications by 1 second
        });
      }
    },
    onError: (error: any) => {
      toast({
        title: "Error",
        description: error.message || "Failed to complete challenge. Please try again.",
        variant: "destructive",
      });
      // Reset completion state on error
      setIsComplete(false);
    },
  });

  const handleComplete = () => {
    const totalTime = 120 - timeSpent;
    completeMutation.mutate({ timeSpent: totalTime });
  };

  const handleTimeUpdate = (remaining: number) => {
    setTimeSpent(remaining);
  };

  const handleBack = () => {
    setLocation("/");
  };

  if (isLoading || !challenge) {
    return (
      <div className="min-h-screen bg-background flex items-center justify-center">
        <div className="text-muted-foreground" data-testid="loading-challenge">Loading challenge...</div>
      </div>
    );
  }

  const category = categoryConfig[challenge.category as keyof typeof categoryConfig];
  const difficulty = difficultyConfig[challenge.difficulty as keyof typeof difficultyConfig];
  const Icon = category?.icon;

  return (
    <div className="min-h-screen bg-background">
      {/* Header */}
      <header className="border-b border-border bg-card/30 backdrop-blur-sm sticky top-0 z-10">
        <div className="container max-w-4xl mx-auto px-4 py-4">
          <Button variant="ghost" onClick={handleBack} data-testid="button-back">
            <ArrowLeft className="h-4 w-4 mr-2" />
            Back
          </Button>
        </div>
      </header>

      <main className="container max-w-4xl mx-auto px-4 py-8">
        {/* Challenge Info */}
        <Card className="border-card-border mb-8">
          <CardHeader>
            <div className="flex items-start justify-between gap-4 flex-wrap">
              <div className="flex items-center gap-3 flex-1 min-w-0">
                {Icon && (
                  <div className="w-14 h-14 rounded-full bg-card flex items-center justify-center shrink-0">
                    <Icon className="h-7 w-7 text-foreground" />
                  </div>
                )}
                <div className="flex-1 min-w-0">
                  <CardTitle className="text-2xl mb-1" data-testid="text-challenge-title">
                    {challenge.title}
                  </CardTitle>
                  <CardDescription>{category?.label}</CardDescription>
                </div>
              </div>
              <Badge variant="secondary">
                <span className={difficulty.color}>{difficulty.label}</span>
              </Badge>
            </div>
          </CardHeader>
          <CardContent className="space-y-4">
            <div>
              <h3 className="text-sm font-medium text-muted-foreground mb-2">Description</h3>
              <p className="text-foreground">{challenge.description}</p>
            </div>
            <div>
              <h3 className="text-sm font-medium text-muted-foreground mb-2">Instructions</h3>
              <p className="text-foreground leading-relaxed">{challenge.instructions}</p>
            </div>
            <div className="flex items-center gap-6 pt-2 border-t border-border">
              <div className="flex items-center gap-2">
                <span className="text-sm text-muted-foreground">Duration:</span>
                <span className="font-semibold text-foreground" data-testid="text-duration">2 minutes</span>
              </div>
              <div className="flex items-center gap-2">
                <span className="text-sm text-muted-foreground">Points:</span>
                <span className="font-semibold text-foreground" data-testid="text-points">{challenge.points}</span>
              </div>
            </div>
          </CardContent>
        </Card>

        {/* Timer Section */}
        <div className="flex flex-col items-center">
          {!isComplete ? (
            <CircularTimer
              duration={120} // 2 minutes
              onComplete={handleComplete}
              onTimeUpdate={handleTimeUpdate}
            />
          ) : (
            <div className="text-center py-12" data-testid="completion-message">
              <div className="w-24 h-24 rounded-full bg-primary/10 flex items-center justify-center mx-auto mb-6 animate-in zoom-in duration-500">
                <CheckCircle2 className="h-12 w-12 text-primary" />
              </div>
              <h2 className="text-3xl font-bold text-foreground mb-2 animate-in fade-in slide-in-from-bottom-4 duration-700" data-testid="text-completion-title">
                Well Done!
              </h2>
              <p className="text-muted-foreground mb-2 animate-in fade-in duration-1000" data-testid="text-completion-message">
                You've completed this challenge and earned <span className="font-bold text-primary">{pointsEarned || challenge.points} points</span>!
              </p>
              {completeMutation.isPending && (
                <p className="text-sm text-muted-foreground mb-8">Updating your progress...</p>
              )}
              {!completeMutation.isPending && (
                <p className="text-sm text-muted-foreground mb-8">Your streak and stats have been updated</p>
              )}
              <div className="flex gap-3 justify-center animate-in fade-in slide-in-from-bottom-4 duration-1000">
                <Button onClick={handleBack} data-testid="button-back-home">
                  Back to Home
                </Button>
                <Button variant="outline" onClick={() => setLocation("/challenges")} data-testid="link-more-challenges">
                  More Challenges
                </Button>
              </div>
            </div>
          )}
        </div>
      </main>
    </div>
  );
}

