} from "@/components/ui/dialog";
import { Input } from "@/components/ui/input";
import { useToast } from "@/hooks/use-toast";
import type { AchievementWithProgress } from "@shared/schema";
import {
  Trophy,
  Award,
  Target,
  Star,
  Flame,
  Zap,
  Crown,
  Sparkles,
  Coins,
  Gem,
  Activity,
